mapper.select:
SELECT

FROM book_borrow_buy a


    a.del_flag = 0

        AND a.requester_id = #{requesterId}


        AND bIns.owner_id = #{ownerId}


        AND a.status = #{status}


        AND a.type = #{type}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_time DESC

mapper.update:
UPDATE book_borrow_buy
SET del_flag = 1
WHERE id = #{id}
